@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }

  html {
    overflow-x: hidden;
    scroll-behavior: smooth;
  }

  body {
    overflow-x: hidden;
    overflow-y: auto;
  }

  /* Parallax Performance Optimizations */
  .parallax-element {
    will-change: transform;
    backface-visibility: hidden;
    perspective: 1000px;
    transform: translateZ(0);
  }

  .parallax-smooth {
    transition: transform 0.1s ease-out;
  }

  /* Enhanced scroll effects for parallax */
  .scroll-effect-parallax {
    opacity: 0;
    transform: translateY(100px);
    transition: all 1.2s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  }

  .scroll-effect-parallax.visible {
    opacity: 1;
    transform: translateY(0);
  }

  /* Optimized animations for better performance */
  @media (prefers-reduced-motion: no-preference) {
    .parallax-bg {
      animation: float 20s ease-in-out infinite;
    }

    .parallax-ornament {
      animation: orbit 15s linear infinite;
    }
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px) rotate(0deg); }
    50% { transform: translateY(-20px) rotate(180deg); }
  }

  @keyframes orbit {
    0% { transform: rotate(0deg) translateX(50px) rotate(0deg); }
    100% { transform: rotate(360deg) translateX(50px) rotate(-360deg); }
  }

  /* Reduce motion for accessibility */
  @media (prefers-reduced-motion: reduce) {
    .parallax-element {
      transform: none !important;
    }

    .parallax-bg,
    .parallax-ornament {
      animation: none !important;
    }
  }

  /* Smooth scroll effects */
  .scroll-effect-fade {
    opacity: 0;
    transform: translateY(50px);
    transition: all 0.8s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .scroll-effect-fade.visible {
    opacity: 1;
    transform: translateY(0);
  }

  .scroll-effect-slide {
    transform: translateX(-100px);
    opacity: 0;
    transition: all 0.6s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .scroll-effect-slide.visible {
    transform: translateX(0);
    opacity: 1;
  }

  .scroll-effect-scale {
    transform: scale(0.8);
    opacity: 0;
    transition: all 0.7s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .scroll-effect-scale.visible {
    transform: scale(1);
    opacity: 1;
  }
}

@layer components {
  .font-display {
    font-family: var(--font-display);
  }

  .font-heading {
    font-family: var(--font-heading);
  }

  .font-serif {
    font-family: var(--font-serif);
  }

  /* Premium colorful text styles without animations */
  .text-gradient-primary {
    background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--secondary)) 50%, hsl(var(--accent)) 100%);
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-size: 100% 100%;
  }

  .text-gradient-hero {
    background: linear-gradient(135deg, #8B5CF6 0%, #06B6D4 25%, #10B981 50%, #F59E0B 75%, #EF4444 100%);
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-size: 100% 100%;
    text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);
    filter: drop-shadow(0 1px 2px rgba(0, 0, 0, 0.4));
  }

  .dark .text-gradient-hero {
    background: linear-gradient(135deg, #A78BFA 0%, #22D3EE 25%, #34D399 50%, #FBBF24 75%, #F87171 100%);
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-size: 100% 100%;
    filter: drop-shadow(0 1px 2px rgba(255, 255, 255, 0.1));
  }

  .text-gradient-cool {
    background: linear-gradient(135deg, #3B82F6 0%, #8B5CF6 50%, #EC4899 100%);
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-size: 100% 100%;
  }

  .text-gradient-warm {
    background: linear-gradient(135deg, #F59E0B 0%, #EF4444 50%, #EC4899 100%);
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-size: 100% 100%;
  }

  .text-gradient-nature {
    background: linear-gradient(135deg, #10B981 0%, #059669 50%, #047857 100%);
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-size: 100% 100%;
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .text-shadow-lg {
    text-shadow: 0 4px 8px rgba(0, 0, 0, 0.8);
  }

  .parallax-element {
    will-change: transform;
  }

  /* Advanced performance optimizations */
  .scroll-optimized {
    contain: layout style paint;
    will-change: transform;
    transform: translateZ(0);
  }

  .gpu-accelerated {
    transform: translateZ(0);
    backface-visibility: hidden;
    will-change: transform;
  }

  /* Parallax effects */
  .parallax-bg {
    will-change: transform;
    transform: translateZ(0);
  }

  .parallax-text {
    will-change: transform;
    transform: translateZ(0);
  }

  /* Smooth transitions */
  .section-transition {
    transition: all 0.8s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  }

  /* Enhanced animations for better performance */
  /* Removed enhanced-gradient for better performance and readability */

  /* Removed glow animation for better performance */

  .animate-slide-up {
    animation: slideUp 0.8s ease-out;
  }

  .animate-fade-in {
    animation: fadeIn 1s ease-out;
  }

  .animate-scale-in {
    animation: scaleIn 0.5s ease-out;
  }

  .animate-particle {
    animation: particle 20s linear infinite;
  }

  .animate-pulse-glow {
    animation: pulse-glow 2s ease-in-out infinite;
  }

  .parallax-container {
    height: 100vh;
    overflow-x: hidden;
    overflow-y: auto;
    perspective: 1px;
  }

  .parallax-layer {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
  }

  .parallax-slow {
    transform: translateZ(-1px) scale(2);
  }

  .parallax-medium {
    transform: translateZ(-0.5px) scale(1.5);
  }

  .parallax-fast {
    transform: translateZ(0);
  }

  .scroll-smooth {
    scroll-behavior: smooth;
    -webkit-overflow-scrolling: touch;
  }

  .glass-morphism {
    background: rgba(255, 255, 255, 0.15);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.3);
    color: rgba(255, 255, 255, 0.95);
  }

  .dark .glass-morphism {
    background: rgba(255, 255, 255, 0.1);
    border: 1px solid rgba(255, 255, 255, 0.2);
    color: rgba(255, 255, 255, 0.95);
  }

  /* Removed premium-gradient classes for better readability and performance */

  .text-visible {
    color: hsl(var(--foreground));
    opacity: 1;
  }

  .text-contrast {
    color: hsl(var(--foreground)) !important;
    text-shadow: 0 2px 4px rgba(0, 0, 0, 0.5) !important;
    opacity: 1 !important;
  }

  .dark .text-contrast {
    color: hsl(var(--foreground)) !important;
    text-shadow: 0 2px 4px rgba(255, 255, 255, 0.2) !important;
    opacity: 1 !important;
  }

  .mesh-gradient {
    background: radial-gradient(ellipse at top left, hsl(var(--primary)) 0%, transparent 50%),
                radial-gradient(ellipse at top right, hsl(var(--secondary)) 0%, transparent 50%),
                radial-gradient(ellipse at bottom left, hsl(var(--accent)) 0%, transparent 50%),
                radial-gradient(ellipse at bottom right, hsl(var(--success)) 0%, transparent 50%);
  }

  .progress-bar {
    transform-origin: left;
    animation: progressFill 2s ease-out forwards;
  }

  .tilt-card {
    transform-style: preserve-3d;
    transition: transform 0.3s ease;
  }

  .tilt-card:hover {
    transform: perspective(1000px) rotateX(5deg) rotateY(5deg) translateZ(10px);
  }

  .scroll-indicator {
    position: fixed;
    top: 0;
    left: 0;
    height: 4px;
    background: linear-gradient(90deg, hsl(var(--primary)), hsl(var(--secondary)), hsl(var(--accent)));
    z-index: 1000;
    transition: width 0.3s ease;
  }

  .particles {
    position: absolute;
    width: 100%;
    height: 100%;
    overflow: hidden;
    top: 0;
    left: 0;
    z-index: 0;
  }

  .particle {
    position: absolute;
    width: 4px;
    height: 4px;
    background: linear-gradient(45deg, hsl(var(--primary)), hsl(var(--secondary)));
    border-radius: 50%;
    animation: particle linear infinite;
    opacity: 0.7;
  }
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-20px); }
}

@keyframes glow {
  from { text-shadow: 0 0 20px hsl(var(--primary)); }
  to { text-shadow: 0 0 30px hsl(var(--secondary)), 0 0 40px hsl(var(--secondary)); }
}

@keyframes slideUp {
  from { transform: translateY(100px); opacity: 0; }
  to { transform: translateY(0); opacity: 1; }
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes scaleIn {
  from { transform: scale(0.8); opacity: 0; }
  to { transform: scale(1); opacity: 1; }
}

@keyframes particle {
  0% { transform: translateY(100vh) translateX(-50px); }
  100% { transform: translateY(-100px) translateX(50px); }
}

@keyframes progressFill {
  from { transform: scaleX(0); }
  to { transform: scaleX(1); }
}

@keyframes gradient {
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
}

@keyframes gradient-shift {
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
}

@keyframes float-slow {
  0%, 100% { transform: translateY(0px) rotate(0deg); }
  50% { transform: translateY(-30px) rotate(5deg); }
}

@keyframes pulse-glow {
  0%, 100% { 
    box-shadow: 0 0 20px hsl(var(--primary)/0.3);
    text-shadow: 0 0 20px hsl(var(--primary)/0.5);
  }
  50% { 
    box-shadow: 0 0 40px hsl(var(--primary)/0.6), 0 0 60px hsl(var(--secondary)/0.3);
    text-shadow: 0 0 30px hsl(var(--secondary)/0.7), 0 0 40px hsl(var(--primary)/0.5);
  }
}



/* Custom Scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: hsl(var(--dark-card));
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(45deg, hsl(var(--primary)), hsl(var(--secondary)));
  border-radius: 4px;
}

/* Loading Screen */
.loader {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background: linear-gradient(135deg, hsl(var(--dark-bg)) 0%, hsl(var(--dark-card)) 100%);
  z-index: 9999;
  transition: opacity 0.5s ease-out;
}

.loader-text {
  font-size: 2rem;
  font-weight: 600;
  background: linear-gradient(45deg, hsl(var(--primary)), hsl(var(--secondary)), hsl(var(--accent)));
  background-size: 300% 300%;
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  animation: gradient 2s ease-in-out infinite;
}
/* Performance optimization styles added */

/* Enhanced text contrast utilities for dark mode */
.text-primary-contrast {
  @apply text-white;
}

.dark .text-primary-contrast {
  @apply text-gray-900;
}

.text-secondary-contrast {
  @apply text-white/80;
}

.dark .text-secondary-contrast {
  @apply text-gray-700;
}

.text-nav-contrast {
  @apply text-white/90;
}

.dark .text-nav-contrast {
  @apply text-gray-800;
}

