import { Phone, Mail, MapPin, Linkedin, Github, Twitter, Send } from "lucide-react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { motion, useScroll, useTransform, useSpring } from "framer-motion";
import { useRef } from "react";

const contactFormSchema = z.object({
  name: z.string().min(2, "Name must be at least 2 characters"),
  email: z.string().email("Please enter a valid email address"),
  subject: z.string().min(5, "Subject must be at least 5 characters"),
  message: z.string().min(10, "Message must be at least 10 characters"),
});

type ContactFormData = z.infer<typeof contactFormSchema>;

export default function Contact() {
  const { toast } = useToast();
  const ref = useRef<HTMLDivElement>(null);

  const { scrollYProgress } = useScroll({
    target: ref,
    offset: ["start end", "end start"]
  });

  // Removed heavy animations for better performance

  const form = useForm<ContactFormData>({
    resolver: zodResolver(contactFormSchema),
    defaultValues: {
      name: "",
      email: "",
      subject: "",
      message: "",
    },
  });

  const handleFormSubmit = async (data: ContactFormData) => {
    // Simulate form submission - in a real frontend-only app, you might use a service like Formspree, Netlify Forms, etc.
    console.log('Contact form submitted:', data);

    toast({
      title: "Message received! ðŸ“§",
      description: "Thank you for your message. I'll get back to you soon!",
    });

    form.reset();
  };

  const onSubmit = async (data: ContactFormData) => {
    await handleFormSubmit(data);
  };

  const contactInfo = [
    {
      icon: Phone,
      label: "Phone",
      value: "+918272049522",
      color: "primary"
    },
    {
      icon: Mail,
      label: "Email",
      value: "aliyaanmohd42@gmail.com",
      color: "secondary"
    },
    {
      icon: MapPin,
      label: "Location",
      value: "India",
      color: "accent"
    }
  ];

  const socialLinks = [
    { icon: Linkedin, href: "https://www.linkedin.com/in/hamd-codes-113658311/", color: "from-primary to-secondary" },
    { icon: Github, href: "https://github.com/hamd845", color: "from-secondary to-accent" },
    { icon: Twitter, href: "https://x.com/CodesHamd23703", color: "from-accent to-success" }
  ];

  return (
    <section ref={ref} id="contact" className="py-32 px-4 relative overflow-hidden min-h-screen scroll-optimized">
      {/* Simple Background */}
      <div className="absolute inset-0 opacity-10">
        <div className="absolute top-0 right-0 w-80 h-80 bg-gradient-to-br from-primary to-secondary rounded-full blur-2xl" />
        <div className="absolute bottom-0 left-0 w-80 h-80 bg-gradient-to-br from-accent to-success rounded-full blur-2xl" />
      </div>

      <div className="max-w-6xl mx-auto relative z-10">
        <motion.div 
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-center mb-20"
        >
          <h2 className="font-display text-5xl md:text-7xl font-black mb-6">
            <span className="text-gradient-hero">
              Get In Touch
            </span>
          </h2>
          <p className="text-secondary-contrast text-xl max-w-3xl mx-auto leading-relaxed">
            Ready to bring your ideas to life? Let's discuss your next project and create something amazing together.
          </p>
        </motion.div>

        <div className="grid md:grid-cols-2 gap-16">
          {/* Contact Info */}
          <motion.div 
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
            className="space-y-12"
          >
            <div>
              <h3 className="font-display text-3xl font-bold mb-8 text-primary-contrast">Contact Information</h3>
              <div className="space-y-6">
