                              {...field}
                              placeholder="Your Name"
                              className="bg-white/10 dark:bg-white/10 border-gray-700/50 dark:border-gray-200/50 focus:border-primary rounded-xl h-12 text-white dark:text-white placeholder:text-white/60 dark:placeholder:text-white/60"
                              data-testid="input-name"
                            />
                          </motion.div>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="email"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="text-lg font-semibold text-primary-contrast">Email</FormLabel>
                        <FormControl>
                          <motion.div whileFocus={{ scale: 1.02 }}>
                            <Input
                              {...field}
                              type="email"
                              placeholder="your@email.com"
                              className="bg-white/10 dark:bg-white/10 border-gray-700/50 dark:border-gray-200/50 focus:border-primary rounded-xl h-12 text-white dark:text-white placeholder:text-white/60 dark:placeholder:text-white/60"
                              data-testid="input-email"
                            />
                          </motion.div>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <FormField
                  control={form.control}
                  name="subject"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-lg font-semibold text-primary-contrast">Subject</FormLabel>
                      <FormControl>
                        <motion.div whileFocus={{ scale: 1.02 }}>
                          <Input
                            {...field}
                            placeholder="Project Discussion"
                            className="bg-white/10 dark:bg-white/10 border-gray-700/50 dark:border-gray-200/50 focus:border-primary rounded-xl h-12 text-white dark:text-white placeholder:text-white/60 dark:placeholder:text-white/60"
                            data-testid="input-subject"
                          />
                        </motion.div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="message"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-lg font-semibold text-primary-contrast">Message</FormLabel>
                      <FormControl>
                        <motion.div whileFocus={{ scale: 1.02 }}>
                          <Textarea
                            {...field}
                            rows={6}
                            placeholder="Tell me about your project..."
                            className="bg-white/10 dark:bg-white/10 border-gray-700/50 dark:border-gray-200/50 focus:border-primary resize-none rounded-xl text-white dark:text-white placeholder:text-white/60 dark:placeholder:text-white/60"
                            data-testid="textarea-message"
                          />
                        </motion.div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="flex justify-center pt-4">
                  <Button
                    type="submit"
                    disabled={form.formState.isSubmitting}
                    className="w-full max-w-md px-8 py-6 bg-primary hover:bg-primary/90 rounded-xl font-semibold text-white text-lg shadow-lg hover:shadow-xl transition-all duration-300"
                    data-testid="button-send-message"
                  >
                    <motion.span 
                      className="flex items-center justify-center space-x-3"
                      animate={form.formState.isSubmitting ? { opacity: [1, 0.5, 1] } : {}}
                      transition={{ duration: 1, repeat: form.formState.isSubmitting ? Infinity : 0 }}
                    >
                      <span>{form.formState.isSubmitting ? "Sending..." : "Send Message"}</span>
                      <Send className="w-5 h-5" />
                    </motion.span>
                  </Button>
                </div>
              </form>
            </Form>
          </motion.div>
        </div>
      </div>
    </section>
  );
}

